import React from "react";
import styled from "@emotion/styled";
import { Spring } from "react-spring";
import { withGesture } from "react-with-gesture";
// import useSpring
import { useSpring, animated, useSpringRef, useTransition, config } from '@react-spring/web';
import { useState, useEffect } from "react";
import { transform } from "framer-motion";
import { Button } from "@mui/material";

function TestSpring() {
    const [open, setOpen] = useState(false);
    const handleClick = (newValue) => {
        setOpen(newValue);
    };

    const transRef = useSpringRef();

    

    const data = ['test1', 'test2', 'test3'];
    // transition for carousel
    // const props = useSpring({
    //    from: {opacity: 1, y: 0},
    //    to: [
    //     { opacity: 0, y: -20},
    //     { opacity: 0, y: 10},
    //     { opacity: 1, y: -5},
    //     { opacity: 1, y: 0}
    //    ],
    //    config: {duration: 150, friction: 14, tension: 120},
    //    ref: transRef
    // });

    const trans = useTransition( open ? data : [], {
        from: {
            opacity: 0, y: 10
        },
        enter: [
            // {opacity: 0, y: 10},
            // {opacity: 0.75, y: -5},
            {opacity: 1, y: 0}
        ],
        leave: {
            opacity: 0, y: -10
        },
        ref: transRef,
        // exitBeforeEnter: true,
        trail: 400 / data.length,
        // config: {
        //     duration: 250,
        //     tension: 120,
        //     friction: 14
        // }
        config: config.gentle
    });

    useEffect(() => {
        transRef.start();
    }, [open]);

    return (
        <div style={{height: '400px', width: '400px'}}>
            {trans((style, index) => (
                <animated.div style={{...style, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
                    <p style={{color: 'black', height: '100%', textAlign: 'center'}}>{data[index]}</p>
                </animated.div>
            ))}
            {/* <animated.div style={{...trans, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
                <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test1</p>
            </animated.div>
            <animated.div style={{...trans, height:'150px', backgroundColor: 'white', zIndex: '2', position: 'relative'}}>
                <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test2</p>
            </animated.div>
            <animated.div style={{...trans, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
                <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test3</p>
            </animated.div> */}
            <Button onClick={() => handleClick(!open)}> click me </Button>
        </div>
    );
}

export default TestSpring;
