import React from "react";
import styled from "@emotion/styled";
import { Spring } from "react-spring";
import { withGesture } from "react-with-gesture";
// import useSpring
import { useSpring, animated, useSpringRef, useTransition, config } from '@react-spring/web';
import { useState, useEffect } from "react";
import { transform } from "framer-motion";
import { Button } from "@mui/material";
import projectsInfo from '../files/projectsInfo.json';

function TestSpring(props) {
  const data = props.data.slides;
  const [activeIndex, setActiveIndex] = useState(0);

  const handleClick = (direction) => {
    if (direction === 'next') {
      setActiveIndex((activeIndex + 1) % data.length);
    }
    // setActiveIndex((prevIndex) => {
    //   if (direction === 'next') {
    //     return (prevIndex + 1) % data.length;
    //   }
    //     // return (prevIndex + 1) % projectsInfo.length
    // });
  };

  const transRef = useSpringRef();

  const mod = (a, b) => {
    return ((a % b) + b) % b;
  }

  const getPresentableData = () => {
    const presentableData = [];
    for (let i = -1; i < 2; i++) {
      presentableData.push(data[mod(activeIndex + i, data.length)]);
    }

    return presentableData;
  };

  const presentableData = getPresentableData();

  console.log(presentableData);
  // transition for carousel
  // const props = useSpring({
  //    from: {opacity: 1, y: 0},
  //    to: [
  //     { opacity: 0, y: -20},
  //     { opacity: 0, y: 10},
  //     { opacity: 1, y: -5},
  //     { opacity: 1, y: 0}
  //    ],
  //    config: {duration: 150, friction: 14, tension: 120},
  //    ref: transRef
  // });

  const trans = useTransition( presentableData, {
    from: { opacity: 1, y: 0 },
    enter: { opacity: 1, y: 0 },
      // {opacity: 0, y: 10},
      // {opacity: 0.75, y: -5}, 
    leave: { opacity: 0, y: -10 },
    update: { y: -10 },
    ref: transRef,
    exitBeforeEnter: true,
    // trail: 400 / data.length,
    // config: {
    //     duration: 250,
    //     tension: 120,
    //     friction: 14
    // }
    config: config.gentle
  });

  useEffect(() => {
    transRef.start();
  }, [activeIndex]);

  return (
    <div style={{height: '400px', width: '400px'}}>
      {trans((style, index) => (
        <animated.div style={{...style, backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
          <p style={{color: 'black', height: '100%', textAlign: 'center'}}>{index.content.name}</p>
        </animated.div>
      ))}
      {/* <animated.div style={{...trans, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
        <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test1</p>
      </animated.div>
      <animated.div style={{...trans, height:'150px', backgroundColor: 'white', zIndex: '2', position: 'relative'}}>
        <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test2</p>
      </animated.div>
      <animated.div style={{...trans, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
        <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test3</p>
      </animated.div> */}
      <Button onClick={() => handleClick('next')}> click me </Button>
    </div>
  );
}

export default TestSpring;
