//     content,
//     offsetRadius,
//     index,
//     animationConfig,
//     moveSlide,
//     delta,
//     down,
//     up
//   }

function testSlide({
  content,
  offsetRadius,
  index,
  animationConfig,
  moveSlide,
  delta,
  down,
  up
}) {
  console.log("NAME:" + content);
  console.log('Offset:' +offsetRadius);
  console.log('INDEX:' + index);
  const offsetFromMiddle = index - offsetRadius;
  console.log('OffMid:' + offsetFromMiddle);
  const totalPresentables = 2 * offsetRadius + 1;
  console.log('Pres:' + totalPresentables);
  const distanceFactor = 1 - Math.abs(offsetFromMiddle / (offsetRadius + 1));
  console.log('DIST:' + distanceFactor);
  console.log('Down:' + down);
  console.log('UP:' + up);
  console.log("DELT:" + delta);
  const offsetCardClick = i => {
    console.log(i);
  };

  const translateYoffset = 50 * (Math.abs(offsetFromMiddle) / (offsetRadius + 1));
  let translateY = -50;

  if (offsetRadius !== 0) {
    if (index === 0) {
      translateY = 0;
    } else if (index === totalPresentables - 1) {
      translateY = -100;
    }
  }
  console.log('Trans:' + translateY);

  if (offsetFromMiddle === 0) {
    translateY += delta[1] / (offsetRadius + 1);
    console.log("NEW TRANS:" + translateY);
    if (translateY > -40) {
      console.log('here');
      moveSlide(-1);
    }
    if (translateY < -100) {
      console.log('here2');
      moveSlide(1);
    }
  }
  if (offsetFromMiddle > 0) {
    translateY += translateYoffset;
  } else if (offsetFromMiddle < 0) {
    translateY -= translateYoffset;
  }

  console.log("NEW TRANS2:" + translateY);

  return (
