    //   {style => (
    //     <SlideContainer
    //       style={{
    //         ...style,
    //         zIndex: Math.abs(Math.abs(offsetFromMiddle) - 2)
    //       }}
    //     >
    //       <SlideCard onClick={() => moveSlide(offsetFromMiddle)}>
    //         {content}
    //       </SlideCard>
    //     </SlideContainer>
    //   )}
    // </Spring>
    <div></div>
  );
}

export default withGesture()(testSlide);
