import * as React from 'react';
// import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Grid } from '@mui/material';
import Divider from '@mui/material/Divider';
import { motion } from 'framer-motion';
import StackedNavbar from './StackedNavbar';
import { useState } from 'react';
import projects from '../files/projects.json';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Info from '@mui/icons-material/Info';
import Paper from '@mui/material/Paper';
import { styled } from '@mui/material/styles';

const offWhite = getComputedStyle(document.body).getPropertyValue('--off-white');
const dark = getComputedStyle(document.body).getPropertyValue('--dark');
const orange = getComputedStyle(document.body).getPropertyValue('--orange2');

const Title = styled(Paper)(({ theme }) => ({
  backgroundColor: dark,
  padding: theme.spacing(1),
  marginLeft: theme.spacing(1),
  textAlign: 'left',
  color: 'white',
  boxShadow: '0px 0px 0px 0px'
}));

const Header = styled(Paper)(({ theme }) => ({
  backgroundColor: dark,
  padding: theme.spacing(1),
  marginRight: theme.spacing(3),
  textAlign: 'left',
  color: 'white',
  boxShadow: '0px 0px 0px 0px'
}));

const Description = styled(Paper)(({ theme}) => ({
  backgroundColor: dark,
  ...theme.typography.body2,
  padding: theme.spacing(1),
  marginRight: theme.spacing(1),
  marginLeft: theme.spacing(1),
  textAlign: 'left',
  color: offWhite,
  boxShadow: '0px 0px 0px 0px',
  opacity: 0.75
}));

const Tool = styled(Paper)(({ theme }) => ({
  backgroundColor: orange,
  padding: theme.spacing(0.5),
  marginRight: theme.spacing(2),
  marginLeft: theme.spacing(2),
  textAlign: 'center',
  color: 'white',
  width: '80%',
  boxShadow: '0px 0px 0px 0px'
}));

function ProjectContainer(props) {
  const tools = props.content.tools.split(', ');

  const createTool = (item) => {
    return (<Tool>item</Tool>);
  };

  return (
    <div className='project-container'>
      <React.Fragment key={props.name}>
        <div className='project-info'>
          <Grid container>
            <Grid item sm={12} md={7}>
              <Title>Title: {props.content.name}</Title>
            </Grid>
            <Grid item md={5}>
              <Header>Date: {props.content.date}</Header>
            </Grid>
            <Grid item md={12}>
              <Description>{props.content.description}</Description>
            </Grid>
            {/* to adjust the height of the paper add a height css parameter */}
            {tools.map((item) => createTool(item))}
            <Grid item >
              <Tool>CSS</Tool>
            </Grid>
          </Grid>
        </div>
      </React.Fragment>
    </div>
  );
}

export default ProjectContainer;
