import * as React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import KeyboardArrowUp from '@mui/icons-material/KeyboardArrowUp';
import Info from '@mui/icons-material/Info';
import GitHub from '@mui/icons-material/GitHub';
import About from './pages/About';

function Sidebar() {
  const [state, setState] = React.useState({
    left: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const sidebarAction = (text) => (event) => {
    if (text === 'GitHub') {
      window.open('https://github.com/ZadynB/WebApp');
    } else if (text === 'Developers') {
      if (About.currentSection !== 'dev_info') {
        console.log('1');
        let dev_info_section = document.getElementById('dev_info');
      } else {
        console.log('2');
        return;
      }
      

    }
  }

  const list = (anchor) => (
    <Box
      sx={{ width: 250 }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        {['Developers', 'GitHub'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton onClick={sidebarAction(text)}>
              <ListItemIcon>
                {index % 2 === 0 ? <Info /> : <GitHub />}
              </ListItemIcon>
              <ListItemText primary={text}/>
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <div className='sidebar'>
      {['left'].map((anchor) => (
        <React.Fragment key={anchor}>
          <Button size='small' variant='outlined' onClick={toggleDrawer(anchor, true)} className='rotate'>
              <KeyboardArrowUp/>
          </Button>
          <Drawer
            anchor={anchor}
            open={state[anchor]}
            onClose={toggleDrawer(anchor, false)}
          >
            {list(anchor)}
          </Drawer>
        </React.Fragment>
      ))}
    </div>
  );
}

export default Sidebar;
