import React from "react";
import styled from "@emotion/styled";
import { Spring } from "react-spring";
import { withGesture } from "react-with-gesture";
// import useSpring
import { useSpring, animated, useSpringRef } from '@react-spring/web';
import { useState, useEffect } from "react";
import { transform } from "framer-motion";
import { Button } from "@mui/material";
import { config } from 'react-spring';

function TestSpring() {
    const [open, setOpen] = useState(false);
    const handleClick = (newValue) => {
        setOpen(newValue);
    };
    const transRef = useSpringRef();

    useEffect(() => {
        transRef.start();
    }, [open]);

    const props1 = useSpring({
       from: {opacity: 0.5, scale: 1},
       to: [
        { opacity: 0, scale: 0},
        { opacity: 0, scale: 0.5},
        { opacity: 1, scale: 1}
       ],
    //    config: config.gentle,
       config: {duration: 250, friction: 14, tension: 120},
       ref: transRef
    });

    const props2 = useSpring({
        from: {opacity: 1, y: 0, scale: 1},
        to: [
         { opacity: 0, y: -30, scale: 0},
         { opacity: 0, y: 20, scale: 0.5},
         { opacity: 1, y: 10, scale: 1}
        ],
        // config: config.gentle,
        config: {duration: 250, friction: 14, tension: 120},
        ref: transRef
     });

     const props3 = useSpring({
        from: {opacity: 0.5, y: 0, scale: 1},
        to: [
         { opacity: 0, y: -10, scale: 0},
         { opacity: 0, y: 20, scale: 0.5},
         { opacity: 1, y: 10, scale: 1}
        ],
        // config: config.gentle,
        config: {duration: 250, friction: 14, tension: 120},
        ref: transRef
     });

    return (
        <div>
            <animated.div style={{...props1, zIndex: 1}} className='test'>
                <p style={{fontSize: '15pt', backgroundColor: 'black'}}>test1</p>
            </animated.div>
            <animated.div style={{...props2, zIndex: 2}} className='test'>
                <p style={{fontSize: '20pt', backgroundColor: 'black'}}>test2</p>
            </animated.div>
            <animated.div style={{...props3, zIndex: 1}} className='test'>
                <p style={{fontSize: '15pt', backgroundColor: 'black'}}>test3</p>
            </animated.div>
            <Button onClick={() => handleClick(!open)}> click me </Button>
        </div>
    );
}

export default TestSpring;
