import React from "react";
import styled from "@emotion/styled";
import { Spring } from "react-spring";
import { withGesture } from "react-with-gesture";
// import useSpring
import { useSpring, animated, useSpringRef, useTransition, config } from '@react-spring/web';
import { useState, useEffect } from "react";
import { transform } from "framer-motion";
import { Button } from "@mui/material";
import projectsInfo from '../files/projectsInfo.json';

function Example(props) {
  const data = props.data.slides;
  const [activeIndex, setActiveIndex] = useState(0);

  const mod = (a, b) => {
    return ((a % b) + b) % b;
  };

  const getPresentableData = () => {
    const presentableData = [];

    for(let i = -1; i < 2; i++) {
      presentableData.push(data[mod(activeIndex + i, data.length)]);
    }
    return presentableData
  };

  const presentableData = getPresentableData();

  const handleClick = (dir) => {
    if (dir === 'up') {
      setActiveIndex((activeIndex + 1) % data.length);
    } else {
      setActiveIndex((activeIndex + (data.length - 1)) % data.length);
    }
  };

  const transRef = useSpringRef();
  const trans = useTransition( presentableData, {
    from: { opacity: 0, y: 10 },
    enter: [{ opacity: 1, y: -5 }, { opacity: 1, y: 0}],
    leave: { opacity: 0, y: -10 },
    update: (item) => ({
      opacity: item.id === presentableData[activeIndex].id ? 0 : 1,
      y: item.id === presentableData[activeIndex].id ? 0 : -10,
    }),
    ref: transRef,
    exitBeforeEnter: true,
    config: {
      duration: 1000,
    }
  });

  useEffect(() => {
    transRef.start();
  }, [activeIndex]);

  return (
    <div style={{height: '400px', width: '400px'}}>
      {trans((style, index) => (
        <animated.div style={{...style, backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
          <p style={{color: 'black', height: '100%', textAlign: 'center'}}>{index.content.name}</p>
        </animated.div>
      ))}
      <Button onClick={() => handleClick('up')}> next </Button>
      <Button onClick={() => handleClick('down')}> prev </Button>
    </div>
  );
}

export default Example;
