import React from "react";
import styled from "@emotion/styled";
import { Spring } from "react-spring";
import { withGesture } from "react-with-gesture";
// import useSpring
import { useSpring, animated } from '@react-spring/web';
import { transform } from "framer-motion";

const SlideContainer = styled.div`
  position: absolute;
  height: 70%;
  top: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: 50% 50%;
`;

const SlideCard = styled.div`
  position: relative;
  max-width: 50%;
  min-width: 30%;
  width: 100vw;
  height: 100%;
  background: white;
  font-size: 35px;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: 50% 50%;
  color: black;
`;

// {
//     content,
//     offsetRadius,
//     index,
//     animationConfig,
//     moveSlide,
//     delta,
//     down,
//     up
//   }

function TestSlide({
  content,
  offsetRadius,
  index,
  animationConfig,
  moveSlide,
  delta,
  down,
  up,
  direction
}) {
  // console.log("NAME:" + content);
  // console.log('Offset:' +offsetRadius);
  // console.log('INDEX:' + index);
  const offsetFromMiddle = index - offsetRadius;
  // console.log('OffMid:' + offsetFromMiddle);
  const totalPresentables = 2 * offsetRadius + 1;
  // console.log('Pres:' + totalPresentables);
  const distanceFactor = 1 - Math.abs(offsetFromMiddle / (offsetRadius + 1));
  // console.log('DIST:' + distanceFactor);
  // console.log('Down:' + down);
  // console.log('UP:' + up);
  // console.log("DELT:" + delta);
  const offsetCardClick = i => {
    console.log(i);
  };

  const translateYoffset = 50 * (Math.abs(offsetFromMiddle) / (offsetRadius + 1));
  let translateY = -50;

  if (offsetRadius !== 0) {
    if (index === 0) {
      translateY = 0;
    } else if (index === totalPresentables - 1) {
      translateY = -100;
    }
  }
  // console.log('Trans:' + translateY);

  if (offsetFromMiddle === 0) {
    translateY += delta[1] / (offsetRadius + 1);
    // console.log("NEW TRANS:" + translateY);
    if (translateY > -40) {
      // console.log('here');
      moveSlide(-1);
    }
    if (translateY < -100) {
      // console.log('here2');
      console.log('here');
      moveSlide(1);
    }
  }

  if (offsetFromMiddle > 0) {
    translateY += translateYoffset;
  } else if (offsetFromMiddle < 0) {
    translateY -= translateYoffset;
  }
  console.log('CONTENT:' + content);
  console.log('TRANS:' + translateY);
  console.log("INDEX:" + index);
  // console.log("NEW TRANS2:" + translateY);

  let str = '';
  if (direction === 'up') {
    if (index === 2) {
      str = `translateX(0%) translateY(${translateY - 20}%) scale(${0})`;
    } else {
      str = `translateX(0%) translateY(${translateY}%) scale(${distanceFactor})`;
    }
  } 
  // else {
  //   if (index === 2) {
  //     str = `scale(1)`;
  //   } else {
  //     str = `scale(0)`;
  //   }
  // }
  

  const props = useSpring({
    // from: {opacity: 0},
    from: {
      opacity: distanceFactor * distanceFactor,
      // transform: `scale(${distanceFactor})`,
      top: `${
        offsetRadius === 0 ? 50 : 50 + (offsetFromMiddle * 50) / offsetRadius
      }%`,
      transform: `translateX(0%) translateY(${translateY}%) scale(${distanceFactor})`,
    },
    to: {
      opacity: distanceFactor * distanceFactor,
      // transform: `scale(${distanceFactor})`,
      top: `${
        offsetRadius === 0 ? 50 : 50 + (offsetFromMiddle * 50) / offsetRadius
      }%`,
      transform: `translateX(0%) translateY(${translateY}%) scale(${distanceFactor})`,
    },
    config: {duration: 250}
    // transform: `translateX(0%) translateY(${translateY}%) scale(${distanceFactor})`,
    // top: `${
    //   offsetRadius === 0 ? 50 : 50 + (offsetFromMiddle * 50) / offsetRadius
    // }%`,
    // opacity: distanceFactor * distanceFactor
    // opacity: Math.abs(Math.abs(offsetFromMiddle) - 2) === 2 ? 1 : 0.5,
    // top: `${
    //   offsetRadius === 0 ? 50 : 50 + (offsetFromMiddle * 50) / offsetRadius
    // }%`,
    // opacity: distanceFactor * distanceFactor,
    // transform: `translateX(0%) translateY(${translateY}%)`,
    // transform: `translate3d(0,${translateY}px,0)`,
    // config: animationConfig
  });
  return (
    <animated.div style={{...props}} className='test'>
      <p>{content}</p>
      {/* <SlideContainer
        style={{zIndex: Math.abs(Math.abs(offsetFromMiddle) - 2)}}
      >
        <SlideCard onClick={() => moveSlide(offsetFromMiddle)}>
          {content}
        </SlideCard>
      </SlideContainer> */}
    </animated.div>
  );
}

export default withGesture()(TestSlide);
