import React from "react";
import styled from "@emotion/styled";
import { Spring } from "react-spring";
import { withGesture } from "react-with-gesture";
// import useSpring
import { useSpring, animated, useSpringRef } from '@react-spring/web';
import { useState, useEffect } from "react";
import { transform } from "framer-motion";
import { Button } from "@mui/material";
import { config } from 'react-spring';

function TestSpring() {
    const [open, setOpen] = useState(false);
    const handleClick = (newValue) => {
        setOpen(newValue);
    };
    const transRef = useSpringRef();

    useEffect(() => {
        transRef.start();
    }, [open]);

    const props = useSpring({
       from: {opacity: 1, y: 0},
       to: [
        { opacity: 0, y: -20},
        { opacity: 0, y: 10},
        { opacity: 1, y: 0},
        // { opacity: 0.5, scale: 1}
       ],
    //    config: config.gentle,
       config: config.gentle,
       ref: transRef
    });

    const props2 = useSpring({
        from: {opacity: 1, scale: 1, top: -25},
        to: [
            { opacity: 0, scale: 1, top: -150},
            { opacity: 0, scale: 1, top: -25},

        //  { opacity: 1, y: 0, scale: 1},
        //  { opacity: 1, y: 0, scale: 1}
            {opacity: 1, y: 0, scale: 1, top: -25}
        ],
        // config: config.gentle,
        config: {duration: 500, friction: 14, tension: 120},
        ref: transRef
     });

     const props3 = useSpring({
        from: {opacity: 0.5, scale: 1, top: -25},
        to: [
         { opacity: 1, scale: 1, top: -150},
         { opacity: 0, scale: 1},
         { opacity: 0, scale: 1, top: -25},
         { opacity: 0.5, scale: 1, top: -25}
        ],
        // config: config.gentle,
        config: {duration: 250, friction: 14, tension: 120},
        ref: transRef
     });

    return (
        <div style={{height: '400px', width: '400px'}}>
            <animated.div style={{...props, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
                <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test1</p>
            </animated.div>
            <animated.div style={{...props, height:'150px', backgroundColor: 'white', zIndex: '2', position: 'relative'}}>
                <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test2</p>
            </animated.div>
            <animated.div style={{...props, height:'150px', backgroundColor: 'white', zIndex: '1', position: 'relative'}}>
                <p style={{color: 'black', height: '100%', textAlign: 'center'}}>test3</p>
            </animated.div>
            <Button onClick={() => handleClick(!open)}> click me </Button>
        </div>
    );
}

export default TestSpring;
